import React, { useState,useEffect  } from "react";
import './App.css';
import { Form, Card, Container, Row, Col } from "react-bootstrap";



const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
  let valid = true;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}

const countErrors = (errors) => {
  let count = 0;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (count = count+1)
  );
  return count;
}

const ContactUsForm = () => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, phone, email, message } = e.target.elements;
    let details = {
      name: name.value,
      phone:phone.value,
      email: email.value,
      message: message.value
    };
    console.log(message.value+" message is here");
    
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });

    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };
  useEffect(() => {
    const script = document.createElement('script');
  
    script.src = "https://hcaptcha.com/1/api.js";
    script.async = true;
  
    document.body.appendChild(script);
  
    return () => {
      document.body.removeChild(script);
    }
  }, []);
  return (
    <div className='wrapper'>
    <div className='form-wrapper'>
      <h2>Create Account</h2>
      <form onSubmit={this.handleSubmit} noValidate>
        <div className='fullName'>
          <label htmlFor="fullName">Full Name</label>
          <input type='text' id='name' name='fullName' onChange={this.handleChange} noValidate />
          {errors.fullName.length > 0 && 
            <span className='error'>{errors.fullName}</span>}
        </div>
        <div className='email'>
          <label htmlFor="email">Email</label>
          <input type='email' id='email' name='email' onChange={this.handleChange} noValidate />
          {errors.email.length > 0 && 
            <span className='error'>{errors.email}</span>}
        </div>
        <div className='phone'>
          <label htmlFor="phone">Mobile Number</label>
          <input type='tel' id='phone' name='phone' onChange={this.handleChange} noValidate />
          {errors.password.length > 0 && 
            <span className='error'>{errors.phone}</span>}
        </div>
        <div className='message'>
          <label htmlFor="message">Message</label>
          <textarea id='message' name='message' onChange={this.handleChange}/>
        </div>
        <div className='info'>
        <div class="h-captcha" data-sitekey="b4da1987-303c-4771-97c1-c576311369e1"></div>
        </div>
        <div className='submit'>
          <button>Create</button>
        </div>
        {this.state.errorCount !== null ? <p className="form-status">Form is {formValid ? 'valid ✅' : 'invalid ❌'}</p> : 'Form not submitted'}
      </form>
    </div>
  </div>
  );


};

export default ContactUsForm;
