import React, { Component , useState ,useEffect } from 'react';
import { render } from 'react-dom';
import './index.css';

class App extends Component {
  render() {
    
    return (
      <ContactUs />
    );
  }
}
const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);

const validateForm = (errors) => {
  let valid = true;
  
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}

const countErrors = (errors) => {
  let count = 0;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (count = count+1)
  );
  return count;
}

class ContactUs extends Component {
  
  constructor(props) {
    super(props);
      this.state = {
      formEmpty: true,
      errorCount: null,
      errors: {
        fullName: '',
        email: '',
        phone: '',
      },
      valid:{
        name:false,
        phone:false,
        email:false        
      }
    };
  }

  handleChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;
    let valid = this.state.valid;
    let formEmpty = this.state.formEmpty;
    switch (name) {
      case 'fullName': 
      valid.name = value.length>5?true:false;
        errors.fullName = 
          value.length < 5
            ? 'Full Name must be 5 characters long!'
            : '';
        break;
      case 'email': 
      valid.email = validEmailRegex.test(value)?true:false;
        errors.email = 
          validEmailRegex.test(value)
            ? ''
            : 'Email is not valid!';
        break;
      case 'phone': 
      valid.phone = value.length === 10?true:false
        errors.phone = 
          value.length < 10
            ? 'phone must be of 10 Digits!'
            : '';
        break;
      default:
        break;
    }
    formEmpty=false;

    this.setState({errors, [name]: value});
  }

  

  handleSubmit = (event) => {
    this.setState({formValid: validateForm(this.state.errors)});
    this.setState({errorCount: countErrors(this.state.errors)});
    // event.preventDefault();
   
  }

  
  render() {
    const {errors,valid} = this.state;
    return (
      <div className='wrapper'>
        <div className='form-wrapper'>
          <h2>Create Account</h2>
          <form onSubmit={this.handleSubmit} noValidate>
            <div className='fullName'>
              <label htmlFor="fullName">Full Name</label>
              <input type='text' id='name' name='fullName' onChange={this.handleChange} noValidate />
              {errors.fullName.length > 0 && 
                <span className='error'>{errors.fullName}</span>}
            </div>
            <div className='email'>
              <label htmlFor="email">Email</label>
              <input type='email' id='email' name='email' onChange={this.handleChange} noValidate />
              {errors.email.length > 0 && 
                <span className='error'>{errors.email}</span>}
            </div>
            <div className='phone'>
              <label htmlFor="phone">Mobile Number</label>
              <input type='tel' id='phone' name='phone' maxlength="10" onChange={this.handleChange} noValidate />
              {errors.phone.length > 0 && 
                <span className='error'>{errors.phone}</span>}
            </div>
            <div className='message'>
              <label htmlFor="message">Message</label>
              <textarea id='message' name='message'/>
            </div>

            <div class="captcha h-captcha" data-sitekey="b4da1987-303c-4771-97c1-c576311369e1"></div>
          
            <div className='submit'>
              <button type="submit">Submit</button>
            </div>
             <p className="form-status">Form is {valid.name && valid.email && valid.phone ? 'valid ✅' : 'invalid ❌'}</p> 
          </form>
        </div>
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));




export default ContactUs;
