import React, { useState } from "react";
import './App.css';
import { Form, Card, Container, Row, Col } from "react-bootstrap";
const ContactUsForm = () => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, phone, email, message } = e.target.elements;
    let details = {
      name: name.value,
      phone:phone.value,
      email: email.value,
      message: message.value
    };
    console.log(message.value+" message is here");
    
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };
  return (
    <Container className='mt-5 p-4' >
       <Row >
    <Col md={{ span: 6, offset: 3 }}>
    <Card>
    <form fluid="md"  md={{ span: 6, offset: 3 }} onSubmit={handleSubmit} id="contactForm">
            <Card.Header className="mb-3" >Contact Us</Card.Header>

        

          <div className="mx-3 form-group">
              <input id="name" type="text" ondblclick="this.value=''" className="form-control" placeholder="First name" pattern="^[A-Za-z\s]{1,}[\.]{0,1}[A-Za-z\s]{0,}$" minlength="3" maxlength="50" required/>
          </div>

          <div className="mx-3 form-group">
              <input id="phone" type="tel" ondblclick="this.value=''" className="form-control" placeholder="Mobile Number" pattern="^\d{10}$" minlength="10" maxlength="10" required />
          </div>

          <div className="mx-3 form-group">
              <input id="email" type="email" ondblclick="this.value=''"  maxlength="575"  className="form-control" placeholder="Enter email" required/>
          </div>

          <div className="mx-3 form-group">
              <textarea id="message" type="text" className="form-control" placeholder="Enter Message" />
          </div>

          <div class="h-captcha" data-sitekey="b4da1987-303c-4771-97c1-c576311369e1"></div>

          <button type="submit" className="btn btn-primary btn-block">Send</button>
        
      </form>
      </Card>
      </Col>
  </Row>
      </Container>
  );


};

export default ContactUsForm;
