import React, { useState } from "react";
import './App.css';
import { Form, Card, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
const ContactUsForm = () => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, phone, email, message } = e.target.elements;
    let details = {
      name: name.value,
      phone:phone.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };
  return (
    <Container>
      
    <Card>
    <form fluid="md"  md={{ span: 6, offset: 3 }} onSubmit={handleSubmit} id="contactForm">
          <h3>Contact Us</h3>

          <Form.Group controlId="formBasicEmail">
					<Form.Label>Email address</Form.Label>
					<Form.Control type="email" placeholder="Enter email" />
					<Form.Text className="text-muted">
						We'll never share your email with anyone else.
    				</Form.Text>
				</Form.Group>

          <div className="form-group">
              <label>First name</label>
              <input id="name" type="text" className="form-control" placeholder="First name" />
          </div>

          <div className="form-group">
              <label>Mobile Number</label>
              <input id="phone" type="Number" className="form-control" placeholder="Mobile Number" />
          </div>

          <div className="form-group">
              <label>Email address</label>
              <input id="email" type="email" className="form-control" placeholder="Enter email" />
          </div>

          <div className="form-group">
              <label>Message</label>
              <textarea type="text" className="form-control" placeholder="Enter Message" />
          </div>

          <button type="submit" className="btn btn-primary btn-block">Send</button>
        
      </form>
      </Card>
      </Container>
  );


};

export default ContactUsForm;
